def tgm_significance(h1, h0):


    # Only consider the positive windows.

    denom = len(h1) # If considering only positive windows, len(h1) will be 96.
    p_values_mat = np.zeros((len(h1), len(h1)))#, dtype=np.ndarray)
    for row in range(len(h1)):#pos_windows:
        for column in range(len(h1)):#pos_windows:
            obs_score = h1[row, column]
            count = 0
            for perm_mat_idx in range(len(h0)):
                perm_score = h0[perm_mat_idx][row, column]
                if perm_score > obs_score:
                    count += 1
            p_value = count / denom
            # p_values_list.append(p_values_list)
            p_values_mat[row, column] = p_value


    # Now do the benjamini-hochberg on the p_values_mat.
    # For now, treat each row of the matrix as a separate p-value list.
    # Only consider positive windows.
    all_mat_pvals_idxs = []
    for i in range(0, 111):
        idxs = [j for j in range(0,111)]
        p_values = p_values_mat[i]#[15:].tolist()
        p_vals_list_asc, p_vals_idx_sort = (list(t) for t in zip(*sorted(zip(p_values, idxs))))
        # print(p_vals_idx_sort)
        reject, pvals_corrected, alph_sidak, alph_bonf = multipletests(p_vals_list_asc, is_sorted=True, method='fdr_bh') # You can change alpha=0.05 to 0.01 for tighter bounds.

        all_mat_pvals_idxs.append([p_vals_idx_sort, reject]) # The length of this list should be 111.


    return all_mat_pvals_idxs

